from gettext import npgettext
import sys
import threading
from PyQt6 import QtWidgets
from PyQt6.QtWidgets import QApplication
import cv2
import numpy


class VideoThread(threading):
    change_pixmap_signal = pyqtSignal(npgettext.ndarray)

    def __init__(self):
        super().__init__()
        self.cap = None
        self._run_flag = True

    def run(self):
        self.cap = cv2.VideoCapture(0, cv2.CAP_DSHOW)
        while self._run_flag:
            ret, cv_img = self.cap.read()
            if ret:
                self.change_pixmap_signal.emit(cv_img)
        self.cap.release()

    def stop(self):
        self._run_flag = False
        self.wait()

    def settings(self):
        self.cap.set(cv2.CAP_PROP_SETTINGS, 1)

    def save(self):
        ret, cv_img = self.cap.read() 
        if ret:
            # Obtener la ruta del directorio actual
            current_directory = os.path.dirname(os.path.abspath(__file__))

            # Concatenar el nombre del archivo a la ruta del directorio actual
            image_path = os.path.join(current_directory, "image.jpg")

            # Guardar la imagen utilizando la ruta obtenida
            cv2.imwrite(image_path, cv_img)

